import React, { useState } from 'react';
import { fashionArray } from './FashionArray'
import { Carousel } from 'react-bootstrap';
import { Link } from 'react-router-dom';
function Fashion() {
    const [index, setIndex] = useState(0);
    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };
    let newFashion = fashionArray;
    return (
        <React.Fragment>
            {/*  fashion section start  */}
            <div className="fashion_section">
                <div id="main_slider" className="carousel slide" data-ride="carousel">

                    <div className="container">
                        <h1 className="fashion_taital">Man & Woman Fashion</h1>
                        <div className="fashion_section_2">
                            <div className="row">
                                <div className="col-lg-4 col-sm-4" >

                                    {
                                        newFashion.map((item) => {
                                            return (
                                                <Carousel activeIndex={index} onSelect={handleSelect}>
                                                    <div className="box_main">
                                                        <h4 className="shirt_text">{item.heading}</h4>
                                                        <p className="price_text">Price <span style={{ color: "#262626" }}>$ {item.price}</span></p>
                                                        <div className="tshirt_img"><img src={item.img} /></div>
                                                        <div className="btn_main">
                                                            <div className="buy_bt"><a href="#">{item.buy}</a></div>
                                                            <div className="seemore_bt"><a href="#">{item.see}</a></div>
                                                        </div>
                                                    </div></Carousel>
                                                    )
                                            })
                                        }
                                                

                                </div>
                            </div>
                        </div>
                    </div>
                    <Link className="carousel-control-prev" to="#main_slider" role="button" data-slide="prev">
                        <i className="fas fa-angle-left"></i>
                    </Link>
                    <Link className="carousel-control-next" to="#main_slider" role="button" data-slide="next">
                        <i className="fas fa-angle-right"></i>
                    </Link>
                </div>
            </div>


            {/*  fashion section end  */}
        </React.Fragment >
    )
}

export default Fashion
